mapper.select:
SELECT b.id as id, b.title as title, b.content as content, b.create_date as createDate, b.status as status, gp.id as groupId, gp.name as groupName, gp.status as groupStatus FROM blog b LEFT JOIN blog_group gp ON b.group_id = gp.id WHERE b.status=1
and b.group_id = #{groupId}
ORDER BY b.create_date DESC

mapper.insert:
INSERT INTO
  blog(title, content, create_date, status, group_id)
VALUES (
  #{title}, #{content}, NOW(), 1, #{groupId}
)

mapper.update:
UPDATE
  blog b
SET
  b.status = 1

      ,b.title = #{title}


      ,b.content = #{content}


      ,b.group_id = #{groupId}

WHERE
  b.id = #{id}
